import './App.css';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import Alert from './components/Alert';
// import About from './components/About';
import React,{useState} from 'react'


function App() {
  const [mode, setmode] = useState("light");
  const [SwitchText, setSwitchText] = useState("dark")
  const [alert, setalert] = useState(null)

  const showAlert=(message,type)=>{
    setalert({
      msg:message,
      type:type
    })

    setTimeout(() => {
      setalert(null);
    }, 500);
  }

  const togglegreen=()=>{
    if(mode==="light"){
      setmode("red")
      // document.body.style.backgroundColor="cyan";
      showAlert("Green theme has been enabled","success")
    }
    else{
      setmode("light")
    }
  }

  const toggleMode = ()=>{
    if(mode==="light"){
      setmode('dark')
      setSwitchText('light')
      document.body.style.backgroundColor="grey"
      showAlert("Dark mode has been enabled","success")
    }
    else{
      setmode('light');
      setSwitchText('dark');
      document.body.style.backgroundColor="white"
      showAlert("Light mode has been enabled","success")
    }
  }

  return (
  <>
  <Navbar title="Textutils" theme={mode} togglemode={toggleMode} switch={SwitchText} toggletheme={togglegreen}/>
  <Alert alert={alert}/>

  <div className="container my-3">
    <TextForm showalert={showAlert} heading="Enter the title below" theme={mode}/>

    {/* <About/> */}
  </div>


  </>

  );
}

export default App;
