import React, {useState} from 'react'

export default function TextForm(props) {

  const handleUpClick = ()=>{
    let newText= text.toUpperCase();
    setText(newText)
  }

  const handledownclick = ()=>{
    let newText2= text.toLowerCase();
    setText(newText2)
  }

  const handlealterclick = ()=>{
    let newText2= text.toLowerCase();

    for(var i=0; i<text.length; i+=2){
      newText2[i] = newText2[i].toUpperCase;
    }
    setText(newText2)
  }

  const handleOnChange=(event)=>{
    setText(event.target.value)
  }

  const [text,setText] = useState("");
  return (
    <>
    <div className="container">
        <h1>{props.heading}</h1>

        <div className="mb-3" padding="10px">
            <textarea className="form-control" value={text} onChange={handleOnChange} id="mybox" rows="8"></textarea>
        </div>

        <div className="container">
        <button className="btn btn-primary" onClick={handleUpClick}>Convert to UpperCase</button>
        <button className="btn btn-primary mx-2" onClick={handledownclick}>Convert to LowerCase</button>
        <button className="btn btn-primary mx-2" onClick={handlealterclick}>Convert to AlternativeCase</button>
        </div>
    </div>

    <div className="container my-3">
      <h2>Your Summary</h2>
      <p>{text.split(" ").length} words and {text.length} characters</p>
      <p>You can read complete paragraph in {0.008 * text.split(" ").length} minutes</p>

      <h2>Preview</h2>
      <p>{text}</p>
    </div>
    </>
  )
}
