import React, {useState} from 'react'

export default function TextForm(props) {

  const handleUpClick = ()=>{
    let newtext= text.toUpperCase();
    setText=(newtext);
  }

  // const handleonlowercase = ()=>{
  //   let newtext= text.toLowerCase();
  //   settext=(newtext);
  // }

  const handleOnChange=(event)=>{
    setText=(event.target.value)
  }

  const [text,setText] = useState("Enter text here");
  return (
    <div>
        <h1>{props.heading}</h1>

        <div className="mb-3" padding="10px">
            <textarea className="form-control" value={text} onChange={handleOnChange} id="mybox" rows="8"></textarea>
        </div>

        <button className="btn btnprimary" onclick={handleUpClick} >Convert to UpperCase</button>
        {/* <button className="btn btnprimary" lowercase={handleonlowercase}>Convert to LowerCase</button> */}

    </div>
  )
}
