import React from 'react'

export default function TextForm(props) {
  return (
    <div>
        <h1>{props.heading}</h1>
        <div class="mb-3">
            <textarea class="form-control" id="mybox" rows="8"></textarea>
            <button className="btn btnprimary">Convert to UpperCase</button>
            <button className="btn btnprimary">Convert to LowerCase</button>
        </div>

    </div>
  )
}
